import React, { useState, useEffect } from 'react';
import { db, auth } from './firebase';
import { collection, addDoc, onSnapshot, doc, updateDoc } from 'firebase/firestore';
import { signInWithEmailAndPassword, signOut, onAuthStateChanged, createUserWithEmailAndPassword } from 'firebase/auth';
import './App.css';

interface Session {
  id: string;
  date: string;
  bestLap: number;
  avgLap: number;
  fiveMinuteStint?: number;
  weather: { temp: number; condition: string };
  notes?: string;
  setup?: { tires: string; suspension: string; favorite: boolean };
}

function App() {
  const [sessions, setSessions] = useState<Session[]>([]);
  const [user, setUser] = useState<any>(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [selectedSession, setSelectedSession] = useState<Session | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [runData, setRunData] = useState({ bestLap: '', avgLap: '', fiveMinuteStint: '' });
  const [notes, setNotes] = useState('');
  const [setup, setSetup] = useState({ tires: '', suspension: '', favorite: false });

  // Monitor auth state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Fetch sessions for the logged-in user
  useEffect(() => {
    if (!user) return;
    const sessionsRef = collection(db, `users/${user.uid}/sessions`);
    const unsubscribe = onSnapshot(sessionsRef, (snapshot) => {
      const sessionData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      })) as Session[];
      setSessions(sessionData);
    });
    return () => unsubscribe();
  }, [user]);

  // Login handler
  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed. Check your credentials.');
    }
  };

  // Sign-up handler
  const handleSignUp = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
    } catch (error) {
      console.error('Sign-up error:', error);
      alert('Sign-up failed. Check your email/password (min 6 characters).');
    }
  };

  // Logout handler
  const handleLogout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  // Create a new session
  const createSession = async () => {
    if (!user) {
      alert('Please log in to add sessions.');
      return;
    }
    try {
      const session = {
        date: new Date().toISOString(),
        bestLap: 12.5,
        avgLap: 13.2,
        weather: { temp: 72, condition: 'Sunny' },
      };
      await addDoc(collection(db, `users/${user.uid}/sessions`), session);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Open modal with session details
  const openModal = (session: Session) => {
    setSelectedSession(session);
    setRunData({
      bestLap: session.bestLap.toString(),
      avgLap: session.avgLap.toString(),
      fiveMinuteStint: session.fiveMinuteStint?.toString() || '',
    });
    setNotes(session.notes || '');
    setSetup({
      tires: session.setup?.tires || '',
      suspension: session.setup?.suspension || '',
      favorite: session.setup?.favorite || false,
    });
    setIsModalOpen(true);
  };

  // Save modal changes
  const saveSession = async () => {
    if (!selectedSession || !user) return;
    try {
      const sessionRef = doc(db, `users/${user.uid}/sessions`, selectedSession.id);
      await updateDoc(sessionRef, {
        bestLap: parseFloat(runData.bestLap) || selectedSession.bestLap,
        avgLap: parseFloat(runData.avgLap) || selectedSession.avgLap,
        fiveMinuteStint: parseFloat(runData.fiveMinuteStint) || undefined,
        notes: notes || undefined,
        setup: { tires: setup.tires, suspension: setup.suspension, favorite: setup.favorite },
      });
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error updating session:', error);
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1 className="header-title">RC Track Pro</h1>
        <p className="header-subtitle">Track your laps, setups, and victories</p>
        {user ? (
          <button onClick={handleLogout} className="text-blue-400 hover:text-blue-300">
            Logout ({user.email})
          </button>
        ) : (
          <div className="flex gap-2 items-center">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              className="p-2 bg-gray-800 rounded text-white"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="p-2 bg-gray-800 rounded text-white"
            />
            <button onClick={handleLogin} className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded">
              Login
            </button>
            <button onClick={handleSignUp} className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded">
              Sign Up
            </button>
          </div>
        )}
      </header>

      <main className="main">
        <section className="sessions-grid">
          {sessions.length === 0 ? (
            <p className="empty-text">
              {user ? 'No sessions yet. Add one to start!' : 'Please log in to view sessions.'}
            </p>
          ) : (
            sessions.map((session) => (
              <div key={session.id} className="session-card">
                <h2 className="session-title">
                  {new Date(session.date).toLocaleDateString()}
                </h2>
                <p className="session-info">Best Lap: {session.bestLap}s</p>
                <p className="session-info">Avg Lap: {session.avgLap}s</p>
                <p className="session-info">
                  Weather: {session.weather.condition}, {session.weather.temp}°F
                </p>
                {session.setup?.favorite && (
                  <span className="text-yellow-400">★ Favorite</span>
                )}
                <button
                  onClick={() => openModal(session)}
                  className="details-button"
                >
                  View Details
                </button>
              </div>
            ))
          )}
        </section>

        {user && (
          <button onClick={createSession} className="new-session-button">
            + New Session
          </button>
        )}

        {isModalOpen && selectedSession && (
          <div className="modal-overlay">
            <div className="modal-content">
              <h2 className="modal-title">Session Details</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-gray-300">Best Lap (s)</label>
                  <input
                    type="number"
                    value={runData.bestLap}
                    onChange={(e) => setRunData({ ...runData, bestLap: e.target.value })}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                  />
                </div>
                <div>
                  <label className="block text-gray-300">Average Lap (s)</label>
                  <input
                    type="number"
                    value={runData.avgLap}
                    onChange={(e) => setRunData({ ...runData, avgLap: e.target.value })}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                  />
                </div>
                <div>
                  <label className="block text-gray-300">5-Minute Stint (s)</label>
                  <input
                    type="number"
                    value={runData.fiveMinuteStint}
                    onChange={(e) => setRunData({ ...runData, fiveMinuteStint: e.target.value })}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                  />
                </div>
                <div>
                  <label className="block text-gray-300">Notes</label>
                  <textarea
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                    rows={3}
                  />
                </div>
                <div>
                  <label className="block text-gray-300">Tires</label>
                  <input
                    type="text"
                    value={setup.tires}
                    onChange={(e) => setSetup({ ...setup, tires: e.target.value })}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                  />
                </div>
                <div>
                  <label className="block text-gray-300">Suspension</label>
                  <input
                    type="text"
                    value={setup.suspension}
                    onChange={(e) => setSetup({ ...setup, suspension: e.target.value })}
                    className="w-full p-2 bg-gray-800 rounded text-white"
                  />
                </div>
                <div className="flex items-center">
                  <input
                    type="checkbox"
                    checked={setup.favorite}
                    onChange={(e) => setSetup({ ...setup, favorite: e.target.checked })}
                    className="mr-2"
                  />
                  <label className="text-gray-300">Favorite Setup</label>
                </div>
              </div>

              <div className="mt-4 flex gap-2">
                <button
                  onClick={saveSession}
                  className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
                >
                  Save
                </button>
                <button
                  onClick={() => setIsModalOpen(false)}
                  className="bg-gray-600 hover:bg-gray-700 px-4 py-2 rounded"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
