import React, { useState, useEffect } from 'react';
import { db } from './firebase';
import { collection, addDoc, onSnapshot } from 'firebase/firestore';

interface Session {
  id: string;
  date: string;
  bestLap: number;
  avgLap: number;
  weather: { temp: number; condition: string };
}

function App() {
  const [sessions, setSessions] = useState<Session[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch sessions from Firestore in real-time
  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'sessions'), (snapshot) => {
      const sessionData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      })) as Session[];
      setSessions(sessionData);
    });
    return () => unsubscribe(); // Cleanup on unmount
  }, []);

  // Create a new session
  const createSession = async () => {
    try {
      const session = {
        date: new Date().toISOString(),
        bestLap: 12.5, // Placeholder, replace with form input later
        avgLap: 13.2,
        weather: { temp: 72, condition: 'Sunny' },
      };
      await addDoc(collection(db, 'sessions'), session);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-black text-white">
      {/* Header */}
      <header className="p-6 bg-red-800 shadow-lg">
        <h1 className="text-3xl font-bold tracking-wide">RC Track Pro</h1>
        <p className="text-sm opacity-75">Track your laps, setups, and victories</p>
      </header>

      {/* Main Content */}
      <main className="p-6">
        {/* Sessions Grid */}
        <section className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {sessions.length === 0 ? (
            <p className="text-gray-400">No sessions yet. Add one to start!</p>
          ) : (
            sessions.map((session) => (
              <div
                key={session.id}
                className="bg-gray-800 p-4 rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 border-l-4 border-red-600"
              >
                <h2 className="text-xl font-semibold">
                  {new Date(session.date).toLocaleDateString()}
                </h2>
                <p className="text-gray-300">Best Lap: {session.bestLap}s</p>
                <p className="text-gray-300">Avg Lap: {session.avgLap}s</p>
                <p className="text-gray-300">
                  Weather: {session.weather.condition}, {session.weather.temp}Â°F
                </p>
                <button
                  onClick={() => setIsModalOpen(true)}
                  className="mt-2 text-sm text-red-400 hover:text-red-300"
                >
                  View Details
                </button>
              </div>
            ))
          )}
        </section>

        {/* New Session Button */}
        <button
          onClick={createSession}
          className="fixed bottom-6 right-6 bg-red-600 hover:bg-red-700 px-6 py-3 rounded-full shadow-lg transition-transform duration-200 hover:scale-105"
        >
          + New Session
        </button>

        {/* Modal (Placeholder for Details) */}
        {isModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-gray-900 p-6 rounded-lg w-full max-w-md">
              <h2 className="text-2xl font-bold mb-4">Session Details</h2>
              <p className="text-gray-300">Coming soon: Runs, setups, and more!</p>
              <button
                onClick={() => setIsModalOpen(false)}
                className="mt-4 bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
